package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.InceptDocTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.InceptDoc;public class InceptDocTableSelectFieldEditor extends AbstractFieldEditor {  /**   *    */  private static final long serialVersionUID = -7677429808342983951L;  private InceptDocTableSelectField editField;  public InceptDocTableSelectFieldEditor(String name) {    super(name);  }  public InceptDocTableSelectFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new InceptDocTableSelectField(20);    editField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return editField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        InceptDoc value = editField.getInceptDoc();        bill.setInceptDocCode(value == null ? null : value.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getInceptDoc();  }  public void setValue(Object value) {    if (value == null) {      editField.setValue(null);    }    else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setValueByCode(bill.getInceptDocCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.INCEPTDOC_CODE;  }}