package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ManageTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.Manage;public class ManageTableSelectFieldEditor extends AbstractFieldEditor {  private static final long serialVersionUID = -7677429808342983951L;  private ManageTableSelectField editField;  public ManageTableSelectFieldEditor(String name) {    super(name);  }  public ManageTableSelectFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new ManageTableSelectField();    editField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return editField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        Manage Manage = editField.getManage();        bill.setManageCode(Manage == null ? null : Manage.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getManage();  }  public void setValue(Object value) {    if (value == null) {      editField.setValue(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setValueByCode(bill.getManageCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.MANAGE_CODE;  }}