package com.ufgov.zc.client.component.ui.fieldeditor;import java.util.List;import javax.swing.JComponent;import javax.swing.JFrame;import javax.swing.JTextField;import javax.swing.UIManager;import com.ufgov.zc.client.component.PayBankAccountSelectField;public class PayBankInfo extends BankInfo {  private List accoTypeList;  public PayBankInfo() {    super("PAY");    processBankField();    addFieldValuceUpdateListener();    label0.setText(" 付款人");    label0.setHorizontalAlignment(JTextField.LEFT);  }  public PayBankInfo(String type) {    super(type);    processBankField();    addFieldValuceUpdateListener();    label0.setText(" 付款人");    label0.setHorizontalAlignment(JTextField.LEFT);  }  public PayBankInfo(String type, List accoTypeList) {    super(type);    this.accoTypeList = accoTypeList;    processBankField();    addFieldValuceUpdateListener();    label0.setText(" 付款人");    label0.setHorizontalAlignment(JTextField.LEFT);  }  public static void main(String[] args) throws Exception {    UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());    JFrame frame = new JFrame("frame");    frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);    frame.setSize(640, 480);    frame.setLocationRelativeTo(null);    frame.getContentPane().add(new PayBankInfo());    frame.setVisible(true);  }  @Override  protected JComponent createAccountEditComponent() {    if (this.accoTypeList == null) {      return new PayBankAccountSelectField(1);    } else {      return new PayBankAccountSelectField(1, this.accoTypeList);    }  }  @Override  protected JComponent createPayAccountEditComponent() {    return new JTextField();  }}