package com.ufgov.zc.client.component.ui.fieldeditor;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.ArrayList;import java.util.List;import javax.swing.JComboBox;import javax.swing.JComponent;import com.ufgov.zc.client.component.PlanTypeComboBox;import com.ufgov.zc.common.dp.model.DpDetail;import com.ufgov.zc.common.system.model.AsVal;public class PmAdjustCodeComboFieldEditor extends AbstractFieldEditor {  private PlanTypeComboBox pmAdjustCodeComboBox;  public PmAdjustCodeComboFieldEditor(String name) {    super(name);  }  public PmAdjustCodeComboFieldEditor(String name, boolean isEnabled) {    super(name);    pmAdjustCodeComboBox.setEnabled(isEnabled);  }  @Override  public Object getValue() {    return pmAdjustCodeComboBox.getSelectedItem();  }  @Override  public void setValue(Object value) {    if (value instanceof DpDetail) {      DpDetail dpDetail = (DpDetail) value;      pmAdjustCodeComboBox.setSelectedAsValByCode(dpDetail.getPmAdjustCode());    }  }  public JComboBox getComboBox() {    return this.pmAdjustCodeComboBox;  }  @Override  protected JComponent createEditorComponent() {    List list = new ArrayList();    list.add("101");    list.add("201");    pmAdjustCodeComboBox = new PlanTypeComboBox(list);    pmAdjustCodeComboBox.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        if (getEditObject() instanceof DpDetail) {          DpDetail dpDetail = (DpDetail) getEditObject();          if (dpDetail != null) {            AsVal asVal = pmAdjustCodeComboBox.getSelectedAsVal();            dpDetail.setPmAdjustCode(asVal == null ? null : asVal.getValId());          }        }      }    });    return pmAdjustCodeComboBox;  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.PM_ADJUST_CODE;  }}