package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.SendDocTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.SendDoc;public class SendDocTableSelectFieldEditor extends AbstractFieldEditor {  /**   *    */  private static final long serialVersionUID = -7677429808342983951L;  private SendDocTableSelectField editField;  public SendDocTableSelectFieldEditor(String name) {    super(name);  }  public SendDocTableSelectFieldEditor(String name, boolean isEnabled) {    super(name);    editField.setEnabled(isEnabled);    editField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    editField = new SendDocTableSelectField(25);    editField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return editField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        SendDoc value = editField.getSendDoc();        bill.setSendDocCode(value == null ? null : value.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return editField.getSendDoc();  }  public void setValue(Object value) {    if (value == null) {      editField.setValue(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      editField.setValueByCode(bill.getSendDocCode());    }  }  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.SENDDOC_CODE;  }}