package com.ufgov.zc.client.component.zc;import java.util.List;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.client.datacache.CompanyDataCache;import com.ufgov.zc.common.zc.foreignentity.IForeignEntityHandler;import com.ufgov.zc.common.zc.model.ZcEbProArgue;public abstract class ZcEbProArgueHandler implements IForeignEntityHandler {  private boolean isMultipleSelect;  private String sqlId = "ZcEbProArgue.fkList";  private String columNames[] = { "项目编号", "项目名称", "采购单位", "负责人" };  public ZcEbProArgueHandler() {    this(false);  }  public ZcEbProArgueHandler(boolean isMultipleSelect) {    this.isMultipleSelect = isMultipleSelect;  }  @SuppressWarnings("unchecked")  public abstract void excute(List selectedDatas);  @SuppressWarnings("unchecked")  public TableModel createTableModel(List showDatas) {    Object data[][] = new Object[showDatas.size()][columNames.length];    for (int i = 0; i < showDatas.size(); i++) {      ZcEbProArgue rowData = (ZcEbProArgue) showDatas.get(i);      int col = 0;      data[i][col++] = rowData.getProCode();      data[i][col++] = rowData.getProName();      data[i][col++] = CompanyDataCache.getName(rowData.getProOrg());      data[i][col++] = rowData.getLeader();    }    MyTableModel model = new MyTableModel(data, columNames) {      private static final long serialVersionUID = -1045880580648402603L;      public boolean isCellEditable(int row, int colum) {        return false;      }    };    return model;  }  public boolean isMultipleSelect() {    return this.isMultipleSelect;  }  public String[] getColumNames() {    return columNames;  }  public void setColumNames(String[] columNames) {    this.columNames = columNames;  }  public String getSqlId() {    return sqlId;  }  public void setSqlId(String sqlId) {    this.sqlId = sqlId;  }}