package com.ufgov.zc.client.component.zc;import java.util.List;import javax.swing.table.TableModel;import com.ufgov.zc.client.common.LangTransMeta;import com.ufgov.zc.client.common.MyTableModel;import com.ufgov.zc.common.system.constants.ZcElementConstants;import com.ufgov.zc.common.zc.foreignentity.IForeignEntityHandler;import com.ufgov.zc.common.zc.model.ZcEbPack;public abstract class ZcEmPackHandler implements IForeignEntityHandler {  private boolean isMultipleSelect;  private String sqlId = "EmEvaluationCondition.packListByMakeCode";  private String columNames[] = { "项目编号", LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PACK_CODE), LangTransMeta.translate(ZcElementConstants.FIELD_TRANS_PACK_NAME) };  public ZcEmPackHandler() {    this(false);  }  public ZcEmPackHandler(boolean isMultipleSelect) {    this.isMultipleSelect = isMultipleSelect;  }  public abstract void excute(List selectedDatas);  public TableModel createTableModel(List showDatas) {    Object data[][] = new Object[showDatas.size()][columNames.length];    for (int i = 0; i < showDatas.size(); i++) {      ZcEbPack rowData = (ZcEbPack) showDatas.get(i);      int col = 0;      data[i][col++] = rowData.getProjCode();      data[i][col++] = rowData.getPackCode();      data[i][col++] = rowData.getPackName();    }    MyTableModel model = new MyTableModel(data, columNames) {      private static final long serialVersionUID = 8627117638515972753L;      public boolean isCellEditable(int row, int colum) {        return false;      }    };    return model;  }  public boolean isMultipleSelect() {    return this.isMultipleSelect;  }  public String[] getColumNames() {    return columNames;  }  public void setColumNames(String[] columNames) {    this.columNames = columNames;  }  public String getSqlId() {    return sqlId;  }  public void setSqlId(String sqlId) {    this.sqlId = sqlId;  }}