package com.ufgov.zc.client.component.zc.fieldeditor;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.OrgComboBox;import com.ufgov.zc.client.component.ui.fieldeditor.AbstractFieldEditor;import com.ufgov.zc.common.commonbiz.model.Org;import com.ufgov.zc.common.system.util.BeanUtil;public class OrgFieldEditor extends AbstractFieldEditor {  private static final long serialVersionUID = 2342342421L;  private OrgComboBox orgField;  public OrgFieldEditor(String name, String fieldName, boolean isEnabled) {    super(name);    this.fieldName = fieldName;    orgField.setEnabled(isEnabled);  }  protected JComponent createEditorComponent() {    orgField = new OrgComboBox();    orgField.initComboBox();    orgField.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        syncObject();      }    });    return orgField;  }  /**   * 同步到对象   */  private void syncObject() {    Object bill = getEditObject();    if (bill != null) {      Org value = orgField.getSelectedOrg();      BeanUtil.set(this.fieldName, value == null ? null : value.getCode(), bill);    }    this.fireEditSynced();  }  public Object getValue() {    return orgField.getSelectedOrg();  }  public void setValue(Object value) {    if (value == null) {      orgField.setSelectedItem(null);    } else {      orgField.setSelectedOrgByCode((String) BeanUtil.get(this.fieldName, value));    }  }}