package com.ufgov.zc.client.console;import java.awt.BorderLayout;import java.awt.Container;import java.awt.Dialog;import java.awt.FlowLayout;import java.awt.Window;import javax.swing.JLabel;import javax.swing.JPanel;import com.ufgov.zc.client.component.GkBaseDialog;import com.ufgov.zc.client.component.SearchConditionSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.SearchCondition;public class SearchConditionListDialog extends GkBaseDialog {  private SearchConditionListDialog self = this;  private SearchConditionListPanel compPanel;  private SearchConditionSelectField selectField = new SearchConditionSelectField(30, "tab");  public SearchConditionListDialog(Window window, String title) {    super(window, Dialog.ModalityType.APPLICATION_MODAL);    setTitle(title);    this.moveToScreenCenter();    this.setSize(560, 440);    this.moveToScreenCenter();    initGUI();  }  public void initGUI() {    Container container = this.getContentPane();    container.setLayout(new BorderLayout());    JPanel topPanel = new JPanel(new FlowLayout(FlowLayout.LEFT));    topPanel.add(new JLabel("页签组名称："));    topPanel.add(selectField);    selectField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        SearchCondition val = selectField.getSearchCondition();        if (val != null) {          String code = val.getConditionId();          compPanel.setConditionId(code);        }        else {          compPanel.setConditionId(null);        }        compPanel.initEditData();      }    });    container.add(topPanel, BorderLayout.NORTH);    container.add(compPanel = new SearchConditionListPanel("", null) {      public void close() {        self.close();      }    }    , BorderLayout.CENTER);    compPanel.initEditData();  }  public void close() {    this.closeDialog();  }}