package com.ufgov.zc.client.console.elementrule;import java.awt.BorderLayout;import java.awt.Dimension;import javax.swing.JFrame;import javax.swing.SwingUtilities;import javax.swing.UIManager;import javax.swing.UnsupportedLookAndFeelException;import com.ufgov.smartclient.plaf.BigButtonSplitPaneUI;import com.ufgov.smartclient.plaf.GrayLookAndFeel;public class Test extends JFrame {  public Test() {    RulePanel panel = new RulePanel();    this.getContentPane().add(panel, BorderLayout.CENTER);    this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);    this.setSize(new Dimension(1000, 700));    this.setLocationRelativeTo(null);    this.setVisible(true);  }  public static void main(String[] args) {    SwingUtilities.invokeLater(new Runnable() {      public void run() {        try {          UIManager.setLookAndFeel(new GrayLookAndFeel());          UIManager.getDefaults().put("SplitPaneUI", BigButtonSplitPaneUI.class.getName());        } catch (UnsupportedLookAndFeelException e) {          e.printStackTrace();        }        Test t = new Test();      }    });  }}