package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.Fund;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class FundDataCache {  private static List dataList;  private static Map dataMap;  private static String showType = AsOptionMeta.getOptVal(BusinessOptionConstants.OPT_CODE_NAME_SHOW_TYPE);  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    ElementConditionDto dto = new ElementConditionDto();    dto.setNd(nd);    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getFund(dto, requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      Fund temp = (Fund) o;      tempMap.put(temp.getCode(), temp);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getFund() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    Fund value = (Fund) dataMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }  public static Fund getBean(String code) {    getData();    Fund value = (Fund) dataMap.get(code);    return value;  }}