package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.Manage;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class ManageDataCache {  private static List dataList;  private static Map dataMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    ElementConditionDto dto = new ElementConditionDto();    dto.setNd(nd);    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getManage(dto, requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      Manage Manage = (Manage) o;      tempMap.put(Manage.getCode(), Manage);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getManage() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    Manage value = (Manage) dataMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }}