package com.ufgov.zc.client.datacache;import java.util.Collections;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.model.Project;import com.ufgov.zc.common.system.RequestMeta;public class ProjectDetailDataCache {  private static List dataList;  private static Map dataMap;  private static void getData() {    if (dataList == null) {      refreshData();    }  }  public static synchronized void refreshData() {    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    dataList = Collections.unmodifiableList(Util.baseDataServiceDelegate.getProjectDetail(requestMeta));    Map tempMap = new HashMap();    for (Object o : dataList) {      Project Project = (Project) o;      tempMap.put(Project.getCode(), Project);    }    dataMap = Collections.unmodifiableMap(tempMap);  }  public static List getProjectDeatil() {    getData();    return dataList;  }  public static String getName(String code) {    getData();    Project value = (Project) dataMap.get(code);    if (value != null) {      return value.getName();    }    return code;  }}