package com.ufgov.zc.client.util;import com.ufgov.zc.client.common.ServiceFactory;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.common.commonbiz.publish.IBaseDataServiceDelegate;import com.ufgov.zc.common.console.model.GkGetdataRule;import com.ufgov.zc.common.system.RequestMeta;public class GkGetDataRuleUtil {  private static GkGetDataRuleUtil gkGetDataRuleUtil = new GkGetDataRuleUtil();  IBaseDataServiceDelegate baseDataServiceDelegate = (IBaseDataServiceDelegate) ServiceFactory.create(  IBaseDataServiceDelegate.class, "baseDataServiceDelegate");  private GkGetDataRuleUtil() {  }  public static synchronized GkGetDataRuleUtil getInstance() {    return gkGetDataRuleUtil;  }  public String getDataRuleCondiStr(String ruleId) {    String result = null;    if (ruleId != null && ruleId.length() > 0) {      RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();      try {        GkGetdataRule rule = baseDataServiceDelegate.getGkGetDataRuleById(ruleId, requestMeta);        if (rule != null) {          String ruleSqlOracle = rule.getRuleSqlOracle();          if (ruleSqlOracle != null && ruleSqlOracle.trim().length() > 0) {            result = " ( " + ruleSqlOracle + " ) ";          }        }      } catch (Exception e) {        return result;      }    }    return result;  }}