package com.ufgov.zc.client.zc.ztb.util;import javax.swing.filechooser.FileFilter;import java.io.File;public class FileChooseFilter extends FileFilter {  private String ext;  public FileChooseFilter() {    this(null);  }  public FileChooseFilter(String ext) {    this.ext = ext;  }  @Override  public boolean accept(File f) {    if (f.isDirectory()) {      return true;    }    if (this.ext == null) {      return true;    }    String locExt = "." + this.ext.toLowerCase();    return f.getAbsolutePath().toLowerCase().endsWith(locExt);  }  @Override  public String getDescription() {    String locExt = this.ext.toLowerCase();    if (locExt.endsWith("xml")) {      return GV.getSimpleMsg("xmlDescription");    } else if (locExt.equals("doc")) {      return GV.getSimpleMsg("wordDocumentDescription");    } else if (locExt.equals("docx")) {      return GV.getSimpleMsg("wordDocumentDescription2007");    } else if (locExt.equals("xls")) {      return GV.getSimpleMsg("excelDocumentDescription");    } else if (locExt.equals("xlsx")) {      return GV.getSimpleMsg("excelDocumentDescription2007");    } else if (locExt.equals("ztb")) {      return GV.getSimpleMsg("ufgovZTBDescription");    } else if (locExt.equals(GV.TEMPLATE_ZIP)) {      return GV.getSimpleMsg("ufgovZBTemplateDescription");    }    return null;  }}