package com.ufgov.zc.common.zc.pagination;/** * * <p>Title: 数据查询的分页接口</p> * <p>Description: </p> * <p>Copyright: Copyright (c) 2007</p> * <p>Company: MiracleSoft</p> * @version 1.0 */public interface Pagination {  /**   * 默认的每页记录数   */  int PAGE_SIZE = 15;  /**   * 获得起始索引   *    * @return 起始索引   */  public int getStartIndex();  /**   * 获得结束索引   *    * @return 结束索引   */  public int getEndIndex();  /**   * 设置每页记录数   * @param perCount 每页显示的记录数   */  public void setPageSize(int pageSize);  /**   * 取得每页记录数。   * @return 每页显示的记录数。   */  public int getPageSize();  /**   * 设置总记录数   * @param totalCount 总记录数   */  public void setTotalSize(int totalCount);  /**   * 获取总记录数   * @return 总记录数   */  public int getTotalSize();  /**   * 取得当前页号   * @return 当前页号   */  public int getCurrentPage();  /**   * 设置当前页号   * @param 当前页号   */  public void setCurrentPage(int currentPage);  /**   * 返回总页面数目。   * @return 总分页数目。    */  public int getTotalPage();  /**   * 根据当前页判断是否有下一页   * @return true - 有下一页；false-没有下一页   */  public boolean hasNextPage();  /**   * 根据当前页判断是否有上一页   * @return true - 有上一页；false-没有上一页   */  public boolean hasPreviousPage();  /**   * 获取上一页页面号   * @return 上一页页面号   */  public int getPreviousPage();  /**   * 获取下一页页面号   * @return 下一页页面号   */  public int getNextPage();}