package com.ufgov.zc.server.bi.service.impl;import java.util.List;import com.ufgov.zc.common.bi.model.BiIncome;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.server.bi.dao.IBiIncomeDao;import com.ufgov.zc.server.bi.service.IBiIncomeService;import com.ufgov.zc.server.system.util.NumUtil;import com.ufgov.zc.server.system.util.RequestMetaUtil;public class BiIncomeService implements IBiIncomeService {  private IBiIncomeDao biIncomeDao;  public List getBiIncome(ElementConditionDto dto) {    return biIncomeDao.getBiIncome(dto);  }  public IBiIncomeDao getBiIncomeDao() {    return biIncomeDao;  }  public void setBiIncomeDao(IBiIncomeDao biIncomeDao) {    this.biIncomeDao = biIncomeDao;  }  public String genBiIncomeId(BiIncome biIncome) {    return NumUtil.getInstance().getNo(RequestMetaUtil.getCompoId(), "BI_INCOME_ID", biIncome);  }  public void saveBiIncome(List insertList, List updateList) {    this.biIncomeDao.updateBiIncome(updateList);    this.biIncomeDao.insertBiIncome(insertList);  }  public void deleteBiIncome(List biIncomeIdList) {    this.biIncomeDao.deleteBiIncome(biIncomeIdList);  }  public void auditBiIncome(List biIncomeList) {    for (int i = 0; i < biIncomeList.size(); i++) {      BiIncome bi = (BiIncome) biIncomeList.get(i);      bi.setAstatusCode("3");    }    this.biIncomeDao.updateBiIncome(biIncomeList);  }  public void unAuditBiIncome(List biIncomeList) {    for (int i = 0; i < biIncomeList.size(); i++) {      BiIncome bi = (BiIncome) biIncomeList.get(i);      bi.setAstatusCode("0");    }    this.biIncomeDao.updateBiIncome(biIncomeList);  }}