package com.ufgov.zc.server.commonbiz.service.impl;import java.util.List;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.server.commonbiz.dao.IMaCpElementMappingDao;import com.ufgov.zc.server.commonbiz.service.IMaCpElementMappingService;import com.ufgov.zc.server.system.util.RequestMetaUtil;public class MaCpElementMappingService implements IMaCpElementMappingService {  private IMaCpElementMappingDao maCpElementMappingDao;  public IMaCpElementMappingDao getMaCpElementMappingDao() {    return maCpElementMappingDao;  }  public void setMaCpElementMappingDao(IMaCpElementMappingDao maCpElementMappingDao) {    this.maCpElementMappingDao = maCpElementMappingDao;  }  public List getMaCpElementMapping(ElementConditionDto dto) {    return maCpElementMappingDao.getMaCpElementMapping(dto);  }  public void save(List maCpElementMappingList, String type, String useType) {    maCpElementMappingDao.delete(RequestMetaUtil.getSvNd(), type, useType);    maCpElementMappingDao.insert(maCpElementMappingList);  }}