package com.ufgov.zc.server.cp.dao.ibatis;import java.sql.SQLException;import java.util.HashMap;import java.util.List;import java.util.Map;import org.springframework.orm.ibatis.SqlMapClientCallback;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ibatis.sqlmap.client.SqlMapExecutor;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.system.exception.DaoException;import com.ufgov.zc.server.cp.dao.ICpPlanAgentDBillDao;public class CpPlanAgentDBillDao extends SqlMapClientDaoSupport implementsICpPlanAgentDBillDao {  public List getCpPlanAgentDBill(String planAgentBillId) {    return this.getSqlMapClientTemplate().queryForList(    "CpPlanAgentDbill.getCpPlanAgentDBill", planAgentBillId);  }  public List getCpPlanAgentDBillForList(List planAgentBillIdList) {    if (planAgentBillIdList.size() == 0) {      throw new DaoException("getCpPlanAgentDBillForList的参数planAgentBillIdList为空");    }    Map map = new HashMap();    map.put("planAgentBillIdList", planAgentBillIdList);    return this.getSqlMapClientTemplate().queryForList(    "CpPlanAgentDbill.getCpPlanAgentDBillForList", map);  }  public void insertCpPlanAgentDBill(final List dBills) {    this.getSqlMapClientTemplate().execute(new SqlMapClientCallback() {      public Object doInSqlMapClient(SqlMapExecutor executor) throws SQLException {        executor.startBatch();        for (int i = 0; i < dBills.size(); i++) {          executor.insert("CpPlanAgentDbill.insert", dBills.get(i));        }        executor.executeBatch();        return null;      }    });  }}