package com.ufgov.zc.server.system.util;import java.util.HashMap;import com.ufgov.zc.common.system.model.AsOption;import com.ufgov.zc.server.system.SpringContext;import com.ufgov.zc.server.system.dao.IAsOptionDao;public class AsOptionUtil {  private IAsOptionDao asOptionDao = (IAsOptionDao) SpringContext.getBean("asOptionDao");  private static AsOptionUtil asOptionUtil;    private static HashMap optionMetaMap=new HashMap();  private AsOptionUtil() {  }  public AsOption getOption(String optionId) {    if(optionMetaMap.containsKey(optionId)){      return (AsOption) optionMetaMap.get(optionId);    }    AsOption op=asOptionDao.getAsOption(optionId);    if(op==null)return null;    optionMetaMap.put(optionId, op);    return op;  }  public AsOption getOptionById(String optionId) {    return getOption(optionId);  }  public String getOptionVal(String optionId) {    String val = "";    AsOption asOption = this.getOption(optionId);    if (asOption != null) {      val = asOption.getOptVal();    }    return val;  }  public synchronized static AsOptionUtil getInstance() {    if (asOptionUtil == null) {      asOptionUtil = new AsOptionUtil();    }    return asOptionUtil;  }}