package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.server.zc.dao.IBaseDao;public class BaseDao extends SqlMapClientDaoSupport implements IBaseDao {  public List query(String sqlId, Object obj) {    return getSqlMapClientTemplate().queryForList(sqlId, obj);  }  public Object dml(String sqlId, Object obj) {    return this.getSqlMapClientTemplate().insert(sqlId, obj);  }  public int delete(String sqlId, Object obj) {    return this.getSqlMapClientTemplate().delete(sqlId, obj);  }  /**   */  public Object insert(String sqlId, Object obj) {    this.getSqlMapClientTemplate().insert(sqlId, obj);    return obj;  }  public Object read(String sqlId, Object obj) {    return this.getSqlMapClientTemplate().queryForObject(sqlId, obj);  }  public int update(String sqlId, Object obj) {    return this.getSqlMapClientTemplate().update(sqlId, obj);  }}