package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.zc.model.ZcAuctionInfo;import com.ufgov.zc.common.zc.model.ZcAuctionRecord;import com.ufgov.zc.server.zc.dao.IZcAuctionDao;public class ZcAuctionDao extends SqlMapClientDaoSupport implements IZcAuctionDao {  public ZcAuctionInfo getAuctionInfo(String auctionId) {    return (ZcAuctionInfo) this.getSqlMapClientTemplate().queryForObject("ZcAuctionInfo.selectAuctionInfoById", auctionId);  }  public List getAuctionRecord(String auctionId) {    return this.getSqlMapClientTemplate().queryForList("ZcAuctionRecord.selectAuctionRecordById", auctionId);  }  public List getAuctionInfoAll() {    return this.getSqlMapClientTemplate().queryForList("ZcAuctionInfo.selectAuctionInfoAll");  }  public ZcAuctionRecord getLowerAuctionPrice(String auctionId) {    return (ZcAuctionRecord) this.getSqlMapClientTemplate().queryForObject("ZcAuctionRecord.selectAuctionLowestPrice", auctionId);  }  public void insertAuctionRecord(ZcAuctionRecord zcAuctionRecord) {    this.getSqlMapClientTemplate().insert("ZcAuctionRecord.inserBidRecord", zcAuctionRecord);  }}