package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.NumLimConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcBBrand;import com.ufgov.zc.server.system.util.NumLimUtil;import com.ufgov.zc.server.zc.dao.IZcBdPinpDao;public class ZcBdPinpDao extends SqlMapClientDaoSupport implements IZcBdPinpDao {  public ZcBdPinpDao() {    super();  }  public List getPinpList(ElementConditionDto dto, RequestMeta meta) {    dto.setNumLimitStr(NumLimUtil.getInstance().getNumLimCondByCoType(dto.getWfcompoId(), NumLimConstants.FWATCH));    List list = getSqlMapClientTemplate().queryForList("ZcBrand.getPinpList", dto);    return list;  }  public void insert(ZcBBrand record) {    getSqlMapClientTemplate().insert("ZcBrand.insert", record);  }  public int deleteByPrimaryKey(String zcBraCode) {    int rows = getSqlMapClientTemplate().delete("ZcBrand.deleteByPrimaryKey", zcBraCode);    return rows;  }  public ZcBBrand selectByPrimaryKey(String zcBraCode) {    ZcBBrand record = (ZcBBrand) getSqlMapClientTemplate().queryForObject("ZcBrand.getBrandInfoByBrandId", zcBraCode);    return record;  }  public int updateByPrimaryKey(ZcBBrand record) {    int rows = getSqlMapClientTemplate().update("ZcBrand.updateByPrimaryKey", record);    return rows;  }}