package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.NumLimConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcBdSppc;import com.ufgov.zc.server.system.util.NumLimUtil;import com.ufgov.zc.server.zc.dao.IZcBdSppcDao;public class ZcBdSppcDao extends SqlMapClientDaoSupport implements IZcBdSppcDao {  public ZcBdSppcDao() {    super();  }  public List getZcBdSppcList(ElementConditionDto dto, RequestMeta meta) {    dto.setNumLimitStr(NumLimUtil.getInstance().getNumLimCondByCoType(dto.getWfcompoId(), NumLimConstants.FWATCH));    List list = getSqlMapClientTemplate().queryForList("ZcBdSppc.getZcBdSppcList", dto);    return list;  }  public void insertZcBdSppc(ZcBdSppc record) {    getSqlMapClientTemplate().insert("ZcBdSppc.insertZcBdSppc", record);  }  public int deleteByPrimaryKey(String zcSppcID) {    int rows = getSqlMapClientTemplate().delete("ZcBdSppc.deleteByPrimaryKey", zcSppcID);    return rows;  }  public ZcBdSppc selectByPrimaryKey(String zcSppcID) {    ZcBdSppc record = (ZcBdSppc) getSqlMapClientTemplate().queryForObject("ZcBdSppc.selectByPrimaryKey", zcSppcID);    return record;  }  public int updateZcBdSppcByPrimaryKey(ZcBdSppc record) {    int rows = getSqlMapClientTemplate().update("ZcBdSppc.updateZcBdSppcByPrimaryKey", record);    return rows;  }}