package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import java.util.Map;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.zc.model.ZcBBrand;import com.ufgov.zc.common.zc.model.ZcBMerchandise;import com.ufgov.zc.server.zc.dao.IZcBrandDao;public class ZcBrandDao extends SqlMapClientDaoSupport implements IZcBrandDao {  public List getBrandsForMenuGoods(String catalogueId) {    return this.getSqlMapClientTemplate().queryForList("ZcBrand.getBrandsForMenuGoods", catalogueId);  }  public ZcBBrand getBrandInfoByBrandId(String brandId) {    return (ZcBBrand) this.getSqlMapClientTemplate().queryForObject("ZcBrand.getBrandInfoByBrandId", brandId);  }  public ZcBBrand getBrandInfoByBrandId(ZcBMerchandise zcBMerchandise) {    return (ZcBBrand) this.getSqlMapClientTemplate().queryForObject("ZcBrand.getBrandInfoByBrandIdAndCataID", zcBMerchandise);  }  public List getBrandsForMenuGoods(Map parameterObject) {    return this.getSqlMapClientTemplate().queryForList("ZcBrand.getBrandsForMenuGoodsByZcSuCode", parameterObject);  }}