package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcEbOptions;import com.ufgov.zc.server.zc.dao.IZcEbOptionsDao;public class ZcEbOptionsDao extends SqlMapClientDaoSupport implements IZcEbOptionsDao {  public List getZcEbProtocolList(ElementConditionDto dto) {    return this.getSqlMapClientTemplate().queryForList("ZcEbOptions.getZcEbPOptionList", dto);  }  public String getZcEbFileId(ElementConditionDto dto) {    return this.getSqlMapClientTemplate().queryForObject("ZcEbOptions.getZcebZbFileList", dto.getProjCode())    .toString();  }  public void deleteZcEbProtocolByProtCode(String protCode) {    this.getSqlMapClientTemplate().delete("ZcEbOptions.deleteZcEbOptionsByProtCode", protCode);  }  public void insertZcEbProtocol(ZcEbOptions zcEbOptions) {    this.getSqlMapClientTemplate().insert("ZcEbOptions.insertZcEbOptions", zcEbOptions);  }  public void updateZcEbOptions(ZcEbOptions zcEbOptions) {    this.getSqlMapClientTemplate().update("ZcEbOptions.updateZcEbOptions", zcEbOptions);  }  public int isExistsFileCode(ElementConditionDto dto) {    return Integer.parseInt(this.getSqlMapClientTemplate().queryForObject("ZcEbOptions.getZcebZbFileList",    dto.getProjCode()).toString());  }  public String getFileCode(ElementConditionDto dto) {    return (this.getSqlMapClientTemplate().queryForObject("ZcEbOptions.getZcEbSeq", dto.getProjCode()))    .toString();  }}