package com.ufgov.zc.server.zc.fileResumeBroken.authentication;import java.text.SimpleDateFormat;import java.util.Date;import com.ufgov.zc.common.zc.model.ZcEbPlan;import com.ufgov.zc.server.system.SpringContext;import com.ufgov.zc.server.zc.service.IZcEbPlanService;public class ProjectBidEndTimeVerify {  private String projCode;  private boolean ifPass;  private String failReason;  private IZcEbPlanService zcEbPlanService;  public String getProjCode() {    return projCode;  }  public void setProjCode(String projCode) {    this.projCode = projCode;  }  public boolean isIfPass() {    return ifPass;  }  public void setIfPass(boolean ifPass) {    this.ifPass = ifPass;  }  public String getFailReason() {    return failReason;  }  public void setFailReason(String failReason) {    this.failReason = failReason;  }  public ProjectBidEndTimeVerify(String projCode) {    setProjCode(projCode);  }  public void startVerify() {    zcEbPlanService = (IZcEbPlanService) SpringContext.getBean("zcEbPlanService");    try {      ZcEbPlan zcEbPlan = zcEbPlanService.getZcEbPlanByProjCode(getProjCode());      SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");      Date currDate = new Date(System.currentTimeMillis());      if (sdf.parse(sdf.format(currDate)).before(sdf.parse(sdf.format(zcEbPlan.getBidEndTime())))) {        setIfPass(true);      } else {        setIfPass(false);        failReason = "项目已经过了投标截止时间！";      }    } catch (Exception e) {      e.printStackTrace();      failReason = "校验项目投标截止时间时出错，详细：" + e.getMessage();    }  }}