package com.ufgov.zc.server.zc.fileResumeBroken.upload;import java.io.File;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.IOException;import java.util.HashMap;import java.util.Map;public class FetchUploadFile {  private Map parameterMap = new HashMap();  private Map returnMap = new HashMap();  private String fileName;  public Map getParameterMap() {    return parameterMap;  }  public void setParameterMap(Map parameterMap) {    this.parameterMap = parameterMap;  }  public Map getReturnMap() {    return returnMap;  }  public void setReturnMap(Map returnMap) {    this.returnMap = returnMap;  }  public String getFileName() {    return fileName;  }  public void setFileName(String fileName) {    this.fileName = fileName;  }  public FetchUploadFile(Map parameterMap, String functionDo) {    setParameterMap(parameterMap);    setFileName((String) parameterMap.get("FILENAME"));  }  public void mkDirs(String filePath) {    File file = new File(filePath);    if (!file.exists()) {      file.mkdirs();    }  }  public void start() {    String filePath = "";    if (parameterMap.get("TOPATH") == null || "".equals(parameterMap.get("TOPATH"))) {      filePath = "uploadFiles";    } else {      filePath = parameterMap.get("TOPATH") + "/";    }    mkDirs(filePath);    File file = new File(filePath + fileName);    int fileLen = 0;    if (file.exists()) {      try {        FileInputStream in = new FileInputStream(file);        fileLen = in.available();        in.close();      } catch (FileNotFoundException e) {        returnMap.put("ERRORMESSAGE", "没有找到指定文件！");        e.printStackTrace();      } catch (IOException e) {        returnMap.put("ERRORMESSAGE", "操作文件出现异常！");        e.printStackTrace();      }    }    returnMap.put("SERVERFILELEN", String.valueOf(fileLen));  }}