package com.ufgov.zc.server.zc.service.impl;import java.util.List;import com.ufgov.zc.common.zc.model.ZcAuctionInfo;import com.ufgov.zc.common.zc.model.ZcAuctionRecord;import com.ufgov.zc.common.zc.model.ZcBMerchandise;import com.ufgov.zc.server.zc.dao.IZcAuctionDao;import com.ufgov.zc.server.zc.dao.IZcBMerchandiseDao;import com.ufgov.zc.server.zc.service.IZcAuctionInfoService;public class ZcAuctionInfoService implements IZcAuctionInfoService {  private IZcBMerchandiseDao zcBMerchandiseDao;  private IZcAuctionDao zcAuctionDao;  public IZcAuctionDao getZcAuctionDao() {    return zcAuctionDao;  }  public void setZcAuctionDao(IZcAuctionDao zcAuctionDao) {    this.zcAuctionDao = zcAuctionDao;  }  public IZcBMerchandiseDao getZcBMerchandiseDao() {    return zcBMerchandiseDao;  }  public void setZcBMerchandiseDao(IZcBMerchandiseDao zcBMerchandiseDao) {    this.zcBMerchandiseDao = zcBMerchandiseDao;  }  public ZcBMerchandise getMerchandiseInfo(String zcMerCode) {    return zcBMerchandiseDao.getMerchandiseInfo(zcMerCode);  }  public ZcAuctionInfo getAuctionInfo(String auctionId) {    return zcAuctionDao.getAuctionInfo(auctionId);  }  public List getAuctionRecord(String auctionId) {    return zcAuctionDao.getAuctionRecord(auctionId);  }  public List getAuctionInfoAll() {    return zcAuctionDao.getAuctionInfoAll();  }  public ZcAuctionRecord getLowerAuctionPrice(String auctionId) {    return zcAuctionDao.getLowerAuctionPrice(auctionId);  }  public void insertAuctionRecord(ZcAuctionRecord zcAuctionRecord) {    zcAuctionDao.insertAuctionRecord(zcAuctionRecord);  }}