package com.ufgov.zc.server.zc.service.impl;import java.util.List;import com.kingdrive.workflow.context.WorkflowContext;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.constants.NumLimConstants;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcProjectSupervision;import com.ufgov.zc.server.system.util.NumLimUtil;import com.ufgov.zc.server.system.workflow.WFEngineAdapter;import com.ufgov.zc.server.zc.dao.IBaseDao;import com.ufgov.zc.server.zc.service.IZcEbProjectSupervisionService;public class ZcEbProjectSupervisionService implements IZcEbProjectSupervisionService {  private IBaseDao baseDao;  private WFEngineAdapter wfEngineAdapter;  public List getModelList(ElementConditionDto dto, RequestMeta requestMeta) {    dto.setNumLimitStr(NumLimUtil.getInstance().getNumLimCondByCoType(dto.getWfcompoId(),    NumLimConstants.FWATCH));    return baseDao.query("ZcProjectSupervision.list", dto);  }  public Object update(Object obj, RequestMeta requestMeta) {    return baseDao.dml("ZcProjectSupervision.finishitem", obj);  }  public void carryDown(ZcProjectSupervision ps, RequestMeta requestMeta) {    ElementConditionDto dto = new ElementConditionDto();    dto.setDattr1(ps.getZcPProMake().getZcMakeCode());    dto.setDattr2(ps.getZcPProMake().getNd() + "");    baseDao.dml("ZcProjectSupervision.carryDown", dto);  }  public void blankOut(ZcProjectSupervision ps, RequestMeta requestMeta) {    WorkflowContext workflowContext = wfEngineAdapter.genCommonWFC("废弃", ps.getZcPProMake(), "ZC_P_PRO_MAKE", requestMeta);    if (workflowContext.getInstanceId() != null && workflowContext.getInstanceId().intValue() > 0) {      wfEngineAdapter.interruptNotJudge(workflowContext);    }    baseDao.dml("ZcProjectSupervision.finishitem", ps);  }  public IBaseDao getBaseDao() {    return baseDao;  }  public void setBaseDao(IBaseDao baseDao) {    this.baseDao = baseDao;  }  public WFEngineAdapter getWfEngineAdapter() {    return wfEngineAdapter;  }  public void setWfEngineAdapter(WFEngineAdapter wfEngineAdapter) {    this.wfEngineAdapter = wfEngineAdapter;  }}