package com.ufgov.zc.server.zc.service.impl;import java.util.List;import java.util.Map;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.EmExpertSelectionBill;import com.ufgov.zc.server.zc.dao.IBaseDao;import com.ufgov.zc.server.zc.service.IZcEmExpertEvaluateService;public class ZcEmExpertEvaluateService implements IZcEmExpertEvaluateService {  private IBaseDao baseDao;  public List getList(ElementConditionDto dto, RequestMeta requestMeta) {    return baseDao.query("EmExpertSelectionBill.listByMakeCode", dto);  }  public EmExpertSelectionBill getModel(Map map, RequestMeta requestMeta) {    return (EmExpertSelectionBill) baseDao.read("EmExpertSelectionBill.read", map);  }  public EmExpertSelectionBill updateExpertEvaluateList(EmExpertSelectionBill bill, RequestMeta requestMeta) {    if (bill.getExpertEvaluationList() != null && bill.getExpertEvaluationList().size() > 0) {      for (int i = 0; i < bill.getExpertEvaluationList().size(); i++) {        baseDao.update("EmExpertEvaluation.update", bill.getExpertEvaluationList().get(i));      }    }    return bill;  }  public List getExpertEvaluationList(Map m, RequestMeta requestMeta) {    return baseDao.query("EmExpertEvaluation.listByEmBillCode", m);  }  public IBaseDao getBaseDao() {    return baseDao;  }  public void setBaseDao(IBaseDao baseDao) {    this.baseDao = baseDao;  }}