package com.ufgov.zc.server.zc.web.action.make;import java.util.HashMap;import java.util.List;import java.util.Map;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.apache.struts.action.ActionForm;import org.apache.struts.action.ActionForward;import org.apache.struts.action.ActionMapping;import org.springframework.web.struts.ActionSupport;import com.ufgov.zc.common.zc.model.ZcPProMakeShow;import com.ufgov.zc.server.zc.service.IZcMakeIndexListService;public class MakeShowAction extends ActionSupport {  private final static Log log = LogFactory.getLog(MakeShowAction.class);  public ActionForward execute(ActionMapping mapping, ActionForm form, HttpServletRequest request, HttpServletResponse response) throws Exception {    request.setCharacterEncoding("GBK");    response.setContentType("text/html;charset=GBK");    IZcMakeIndexListService zcMakeIndexListService = (IZcMakeIndexListService) this.getWebApplicationContext().getBean("zcMakeIndexListService");    String forward = "success";    String zcMakeCode = request.getParameter("zcMakeCode");    Map parameterObject = new HashMap();    parameterObject.put("zcMakeCode", zcMakeCode);    List zcPProMakeList = zcMakeIndexListService.findMakebyCode(parameterObject);    request.setAttribute("zcPProMakeList", zcPProMakeList);    ZcPProMakeShow zcPProMakeShow = new ZcPProMakeShow();    if (zcPProMakeList.size() > 0) {      zcPProMakeShow = (ZcPProMakeShow) zcPProMakeList.get(0);    }    request.setAttribute("zcPProMakeShow", zcPProMakeShow);    return mapping.findForward(forward);  }}