package com.ufgov.zc.server.zc.web.action.menugoods;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.apache.struts.action.ActionForm;import org.apache.struts.action.ActionForward;import org.apache.struts.action.ActionMapping;import com.ufgov.zc.server.zc.service.IMenuGoodsService;import com.ufgov.zc.server.zc.web.action.StrutsAction;import com.ufgov.zc.server.zc.web.form.MerchandiseForm;public class SpMerEditAction extends StrutsAction {  private final static Log log = LogFactory.getLog(SpMerEditAction.class);  public ActionForward execute(ActionMapping mapping, ActionForm form, HttpServletRequest request, HttpServletResponse response) throws Exception {    String forward = "success";    String zcMerCode = request.getParameter("zcMerCode");    MerchandiseForm merchandiseForm = (MerchandiseForm) form;    IMenuGoodsService menuGoodsService = (IMenuGoodsService) this.getWebApplicationContext().getBean("menuGoodsService");    merchandiseForm = menuGoodsService.findMerchanDiseBy(zcMerCode);    request.setAttribute("ZC_MER_CG_PRICE", merchandiseForm.getZcBMerPrice().getZC_MER_CG_PRICE());    request.setAttribute("ZcBMerchandise", merchandiseForm.getZcBMerchandise());    request.setAttribute("ZcBMerPrice", merchandiseForm.getZcBMerPrice());    request.setAttribute("ZcBMerDiscount", merchandiseForm.getZcBMerDiscount());    return mapping.findForward(forward);  }}