package com.ufgov.zc.server.zc.web.form;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.system.model.AsVal;import com.ufgov.zc.common.zc.model.ZcBAgency;import com.ufgov.zc.common.zc.pagination.WebPagination;import com.ufgov.zc.server.system.SpringContext;import com.ufgov.zc.server.system.service.IAsValService;import com.ufgov.zc.server.zc.service.IZcBAgencyService;public class AgencyFormBean extends AbstractActionForm {  private static final long serialVersionUID = 261453011975465428L;  private IZcBAgencyService zcBAgencyService;  IAsValService asValService = null;  private List agencyAuditList = new ArrayList();  private List agencyNotAuditList = new ArrayList();  private Integer countTotalNum;  private Integer countAuditNum;  private Integer countNotAuditNum;  private WebPagination pagination;  private Integer countNum;  private List agencyList = new ArrayList();  private ZcBAgency agency = new ZcBAgency();  public String indexListAgency() {    zcBAgencyService = (IZcBAgencyService) SpringContext.getBean("zcBAgencyService");    ElementConditionDto dto = new ElementConditionDto();    dto.setZcText4("1");    agencyAuditList = zcBAgencyService.indexListAgency(dto);    ElementConditionDto dto1 = new ElementConditionDto();    dto1.setZcText4("0");    agencyNotAuditList = zcBAgencyService.indexListAgency(dto1);    Map parameterObject = new HashMap();    parameterObject.put("status", "0");    countNotAuditNum = zcBAgencyService.getAgencyCount(parameterObject);    parameterObject.put("status", "1");    countAuditNum = zcBAgencyService.getAgencyCount(parameterObject);    parameterObject.put("status", "");    countTotalNum = zcBAgencyService.getAgencyCount(parameterObject);    return SUCCESS;  }  public String agencyDetails() {    zcBAgencyService = (IZcBAgencyService) SpringContext.getBean("zcBAgencyService");    asValService = (IAsValService) SpringContext.getBean("asValService");    String zcAgeyCode = this.getRequest().getParameter("zcAgeyCode");    agency = zcBAgencyService.getAgencyById(zcAgeyCode);    List gList = asValService.getAsVal("ZC_VS_AGENCY_TYPE");    for (int i = 0; i < gList.size(); i++) {      AsVal val = (AsVal) gList.get(i);      if (val.getValId().equals(agency.getZcAgeyType()))        agency.setZcAgeyType(val.getVal());    }    agencyList.add(agency);    return SUCCESS;  }  public String toAgencyRegPage() {    String forward = "regRulePage";    return forward;  }  public String regAgency() {    String forward = "regPage";    return forward;  }  public IZcBAgencyService getZcBAgencyService() {    return zcBAgencyService;  }  public void setZcBAgencyService(IZcBAgencyService zcBAgencyService) {    this.zcBAgencyService = zcBAgencyService;  }  public List getAgencyAuditList() {    return agencyAuditList;  }  public void setAgencyAuditList(List agencyAuditList) {    this.agencyAuditList = agencyAuditList;  }  public List getAgencyNotAuditList() {    return agencyNotAuditList;  }  public void setAgencyNotAuditList(List agencyNotAuditList) {    this.agencyNotAuditList = agencyNotAuditList;  }  public Integer getCountTotalNum() {    return countTotalNum;  }  public void setCountTotalNum(Integer countTotalNum) {    this.countTotalNum = countTotalNum;  }  public Integer getCountAuditNum() {    return countAuditNum;  }  public void setCountAuditNum(Integer countAuditNum) {    this.countAuditNum = countAuditNum;  }  public Integer getCountNotAuditNum() {    return countNotAuditNum;  }  public void setCountNotAuditNum(Integer countNotAuditNum) {    this.countNotAuditNum = countNotAuditNum;  }  public IAsValService getAsValService() {    return asValService;  }  public void setAsValService(IAsValService asValService) {    this.asValService = asValService;  }  public List getAgencyList() {    return agencyList;  }  public void setAgencyList(List agencyList) {    this.agencyList = agencyList;  }  public ZcBAgency getAgency() {    return agency;  }  public void setAgency(ZcBAgency agency) {    this.agency = agency;  }  public WebPagination getPagination() {    return pagination;  }  public void setPagination(WebPagination pagination) {    this.pagination = pagination;  }  public Integer getCountNum() {    return countNum;  }  public void setCountNum(Integer countNum) {    this.countNum = countNum;  }}